INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (80, 26, 'Alle Zutaten und Utensilien bereitstellen.', 0, 2),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (81, 26, 'Gemüse und Kräuter waschen.', 0, 3),
