INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (85, 26, 'Zwiebel in Streifen schneiden, Kartoffeln und Pastinaken in 2 cm große Würfel schneiden.', 0, 7),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (86, 26, 'Blätter der Petersilie abzupfen und grob hacken.', 0, 8),
