INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (97, 26, 'Topfdeckel aufsetzen und 15 – 20 Min. weiterköcheln lassen, ab und an umrühren.', 0, 19),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (98, 26, 'Linsen in einem Sieb mit kaltem Wasser abspülen, bis dieses klar hindurchfließt.', 0, 20),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (99, 26, '7 Min. vor Ende der Garzeit Linsen und den Baby-Grünkohl dazugeben, Deckel wieder aufsetzten.', 0, 21),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (100, 26, 'Zum Schluss ½ der Kräuter und Rucola zufügen und alles gut umrühren.', 0, 22),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (101, 26, 'Evtl. noch mit etwas Salz, Pfeffer und etwas Zucker abschmecken.', 0, 23),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (102, 26, 'Linsen-Gemüse-Suppe auf tiefe Teller verteilen, mit restlichen Kräutern bestreuen und genießen.', 0, 24),
