INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (437, 71, 'Crema:', 1, 1),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (438, 71, 'Preparar una cafetera de café de 6 tasas.', 0, 2),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (439, 71, 'Separar las claras de las yemas.', 0, 3),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (440, 71, 'Batir las yemas en un bol hondo con las barillas hasta que se queden blanquecinas o espumosas.', 0, 4),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (441, 71, 'Verter la leche condensada y mezclar.', 0, 5),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (442, 71, 'Verter la nata montada y mezclar con movimientos envolventes.', 0, 6),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (443, 71, 'Coger dos cucharadas pequeñas de café, verter en una taza pequeña y diluir ahí los sobres de nescafé.', 0, 7),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (444, 71, 'Echar la mezcla en la misma lata de la leche condensada, y remover con los restos de leche condensada.', 0, 8),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (445, 71, 'Verter la mezcla en la masa y remover.', 0, 9),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (446, 71, 'Por último batir las claras a punto de nieve y mezclársela a la crema también con movimientos envolventes para que quede espesita.', 0, 10),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (447, 71, 'Tarta:', 1, 11),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (448, 71, 'Verter el café en un plato hondo y en una bandeja rectangular mediana (con un poquito de altura) colocar en el fondo una base con las galletas mojadas en el café (sin azúcar).', 0, 12),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (449, 71, 'Luego vaciar encima la mitad de la crema.', 0, 13),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (450, 71, 'Volver a colocar (en sentido contrario) otra capa de galletas mojadas.', 0, 14),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (451, 71, 'Verter encima el resto de la crema.', 0, 15),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (452, 71, 'por último machacar 4 o 5 galletas y rociar por encima a modo de crocanti.', 0, 16),
INSERT INTO recipe_step (id, recipe_id, description, is_section, sort_order) VALUES (453, 71, 'Taparla con papel de platina y meter en la nevera hasta el día siguiente.', 0, 17),
