BEGIN TRANSACTION;

DROP TABLE recipe_tag;
DROP TABLE recipe_step;
DROP TABLE recipe_ingredient;
DROP TABLE recipe;
DROP TABLE placement;
DROP TABLE ingredient;
DROP TABLE unit;
DROP TABLE tag;
DROP TABLE store;
DROP TABLE aisle;

CREATE TABLE IF NOT EXISTS aisle (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL,
);
CREATE UNIQUE INDEX IF NOT EXISTS unique_aisle_name ON aisle (name)

CREATE TABLE IF NOT EXISTS store (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL,
);
CREATE UNIQUE INDEX IF NOT EXISTS unique_store_name ON store (name)

CREATE TABLE IF NOT EXISTS tag (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL,
);
CREATE UNIQUE INDEX IF NOT EXISTS unique_tag_name ON tag (name)

CREATE TABLE IF NOT EXISTS unit (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name_singular TEXT NOT NULL,
  name_plural TEXT NOT NULL,
);
CREATE UNIQUE INDEX IF NOT EXISTS unique_unit_name_singular ON unit (name_singular)
CREATE UNIQUE INDEX IF NOT EXISTS unique_unit_name_plural ON unit (name_plural)

CREATE TABLE IF NOT EXISTS ingredient (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL,
  aisle_id INTEGER NOT NULL,
  FOREIGN KEY (aisle_id) REFERENCES aisle (id)
);
CREATE UNIQUE INDEX IF NOT EXISTS unique_ingredient_name ON ingredient (name)

CREATE TABLE IF NOT EXISTS placement (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  aisle_id INTEGER NOT NULL,
  store_id INTEGER NOT NULL,
  sort_order INTEGER NOT NULL,
  FOREIGN KEY (aisle_id) REFERENCES aisle (id),
  FOREIGN KEY (store_id) REFERENCES store (id)
);
CREATE UNIQUE INDEX IF NOT EXISTS unique_placement ON placement (aisle_id, store_id)

CREATE TABLE IF NOT EXISTS recipe (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL,
  portions INTEGER NOT NULL,
  duration INTEGER,
  url TEXT,
);

CREATE TABLE IF NOT EXISTS recipe_ingredient (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  recipe_id INTEGER NOT NULL,
  ingredient_id INTEGER NOT NULL,
  quantity NUMERIC,
  unit_id INTEGER,
  sort_order INTEGER NOT NULL,
  FOREIGN KEY (ingredient_id) REFERENCES ingredient (id),
  FOREIGN KEY (recipe_id) REFERENCES recipe (id),
  FOREIGN KEY (unit_id) REFERENCES unit (id)
);

CREATE TABLE IF NOT EXISTS recipe_step (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  recipe_id INTEGER NOT NULL,
  description TEXT NOT NULL,
  is_section INTEGER NOT NULL,
  sort_order INTEGER NOT NULL,
  FOREIGN KEY (recipe_id) REFERENCES recipe (id)
);

CREATE TABLE IF NOT EXISTS recipe_tag (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  recipe_id INTEGER NOT NULL,
  tag_id INTEGER NOT NULL,
  FOREIGN KEY (recipe_id) REFERENCES recipe (id),
  FOREIGN KEY (tag_id) REFERENCES tag (id)
);
CREATE UNIQUE INDEX IF NOT EXISTS unique_recipe_tag ON recipe_tag (recipe_id, tag_id)












COMMIT TRANSACTION;